void cppAddRegistryKey() {
	print("############################### EXAMPLES ###############################");
	print("# Hive  : HKEY_CURRENT_USER | HKEY_LOCAL_MACHINE                       #");
	print("# Path  : Software\\Microsoft\\Windows\\CurrentVersion\\Run                #");
	print("# Name  : PowerShell                                                   #");
	print("# Value : C:\\Windows\\System32\\WindowsPowerShell\\v1.0\\powershell.exe    #");
	print("########################################################################");
	print("# Hive  : HKEY_CURRENT_USER | HKEY_LOCAL_MACHINE                       #");
	print("# Path  : Software\\Policies\\Microsoft\\Windows\\Installer                #");
	print("# Name  : AlwaysInstallElevated                                        #");
	print("# Value : 1                                                            #");
	print("########################################################################");
	print("");
	print("############################ REGISTRY HIVES ############################");
	print("# [1] HKEY_CLASSES_ROOT                                                #");
	print("# [2] HKEY_CURRENT_USER                                                #");
	print("# [3] HKEY_LOCAL_MACHINE                                               #");
	print("# [4] HKEY_USERS                                                       #");
	print("# [5] HKEY_CURRENT_CONFIG                                              #");
	print("########################################################################");
	std::string choice = input("Choose registry hive");
	print("");
	HKEY hKey = NULL;
	if (choice == "1") {
		hKey = HKEY_CLASSES_ROOT;
	} else if (choice == "2") {
		hKey = HKEY_CURRENT_USER;
	} else if (choice == "3") {
		hKey = HKEY_LOCAL_MACHINE;
	} else if (choice == "4") {
		hKey = HKEY_USERS;
	} else if (choice == "5") {
		hKey = HKEY_CURRENT_CONFIG;
	} else {
		print("Invalid choice");
	}
	if (hKey != NULL) {
		std::string path = input("Enter key path");
		print("");
		std::string name = input("Enter key name");
		print("");
		std::string value = input("Enter key value");
		print("");
		std::string confirm = input("Confirm (yes)");
		if (confirm == "yes") {
			print("");
			addRegistryKey(hKey, path, name, value);
		}
	}
}

